import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Gallery() {
  const [images, setImages] = useState([]);
  const [info, setInfo] = useState([]);
  const [error, setError] = useState(''); // State for error messages

  useEffect(() => {
    fetchImages(); // Fetch images when the component mounts
  }, []);

  const fetchImages = async () => {
    try {
        const imageResponse = await axios.get('http://localhost:3000/retrieve-images');
        setImages(imageResponse.data);
    } catch (error) {
        console.error('Error fetching images:', error);
        setError('Error fetching images. Please try again.'); // Set error message
    }

    try {
      const infoResponse = await axios.get('http://localhost:3000/gallery-info');
      setInfo(infoResponse.data);
  } catch (error) {
      console.error('Error fetching images:', error);
      setError('Error fetching images. Please try again.'); // Set error message
  }
};

return (
  <div className="gallery">
    <h2>Image Gallery</h2>
    <div className="image-grid">
      {images.map((image) => (
        <div className="image-item" key={image._id}>
          <img className="gallery-image" src={image.url} alt={`Image ID: ${image._id}`} />
          <div className="image-details">
            <p>Result: {info.result}</p>
            <p>Probability: {info.probability}%</p>
            <p>Username: {info.username}</p>
            <p>Organization: {info.organization}</p>
            <p>Date: {info.date}</p>
          </div>
        </div>
      ))}
    </div>
  </div>
);
}

export default Gallery;
